import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  private baseUrl = 'http://localhost:8000/'

  httpHeaders = new HttpHeaders({'COntent-Type' : 'application/json'});

  constructor(private http: HttpClient) { }

  getAllMembers(): Observable<any> {
    return this.http.get(this.baseUrl + 'members/', {headers: this.httpHeaders})
  }

  getMember(id: any): Observable<any>{
    return this.http.get(this.baseUrl + 'members/' + id + '/', {headers: this.httpHeaders})
  }

  updateMember(member: any): Observable<any>{
    let body = { name: member.name, surname: member.surname, phone: member.phone }
    return this.http.put(this.baseUrl + 'members/' + member.id + '/', member, {headers: this.httpHeaders})
  }

}
