import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { AppComponent } from '../app.component';

@Component({
  selector: 'app-new-member',
  templateUrl: './new-member.component.html',
  styleUrls: ['./new-member.component.css']
})
export class NewMemberComponent implements OnInit{

  public member = {name: '', surname: '', adress: '', phone: ''}

  constructor(private api: ApiService,
              private appComponent: AppComponent){}

  ngOnInit(): void {

  }

  save(){
    this.api.saveMember(this.member).subscribe(
      data => {
        this.appComponent.members.push(data)
      },
      error => {
        console.log("Aconteceu um erro", error.message)
      }
    )
  }

}
<h1>New Member</h1>

<label>Nome:</label>
<input type="text" [(ngModel)]="member.name"><br>
<label>Sobrenome:</label>
<input type="text" [(ngModel)]="member.surname"><br>
<label>Endereço:</label>
<input type="text" [(ngModel)]="member.adress"><br>
<label>Telefone:</label>
<input type="text" [(ngModel)]="member.phone"><br>
<button (click)="save()" class="button confirm">Save Member</button>
